function com_uninstall()
{
	global $mainframe;
	$db =& JFactory::getDBO();
	$db_prefix = $mainframe->getCfg('dbprefix');
	$result = _runQueries($db);
	if(count($result)>=1)
	{
		foreach($result as $msg)
		{
			echo $msg."<br/>\n";
		}
	}else{
		echo "UnInstall Component Success";
	}
}

function _runQueries($db)
{
	
$installQueries[] = 'DROP TABLE `#__agenda_category`';
$installQueries[] = 'DROP TABLE `#__agenda_event`';
	$msg = array();
	if( count($installQueries) >=1 )
	{
		foreach ( $installQueries as $query ) {
			$db->setQuery( $query );
			$db->query();
			if ( $db->getErrorNum() ) {
				$msg[] = '<br/>' . $db->stderr();
			}
		}
	}
	return $msg;
}

